    private String getResponseStr(HttpUriRequest request, String defaultCharset) {
        String responseStr = null;
        CloseableHttpClient httpclient = HttpClients.createDefault();
        try {
            CloseableHttpResponse response = httpclient.execute(request);
            if (response.getStatusLine().getStatusCode() == 200) {
                responseStr = EntityUtils.toString(response.getEntity(), defaultCharset);
            }
            response.close();
        } catch (Exception e) {
        } finally {
            try {
                httpclient.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return responseStr;
        }
    }

